def get_media_path(filename):
    """–í–æ–∑–≤—Ä–∞—â–∞–µ—Ç –ø–æ–ª–Ω—ã–π –ø—É—Ç—å –∫ –º–µ–¥–∏–∞—Ñ–∞–π–ª—É"""
    if not filename:
        return None
    path = os.path.join(MEDIA_DIR, filename)
    print(f"[DEBUG] –ü—Ä–æ–≤–µ—Ä–∫–∞ —Ñ–∞–π–ª–∞: {path}")
    return path if os.path.exists(path) else None


def send_media(chat_id, media_data, caption=None, markup=None):
    """–£–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω–∞—è –æ—Ç–ø—Ä–∞–≤–∫–∞ –º–µ–¥–∏–∞"""
    try:
        if not media_data or 'file' not in media_data:
            bot.send_message(chat_id, "üö´ –ú–µ–¥–∏–∞—Ñ–∞–π–ª –Ω–µ –Ω–∞–π–¥–µ–Ω")
            return

        file_path = get_media_path(media_data['file'])
        if not file_path:
            bot.send_message(chat_id, "üö´ –§–∞–π–ª –Ω–µ –Ω–∞–π–¥–µ–Ω")
            return

        # –ü—Ä–æ–≤–µ—Ä–∫–∞ —Ä–∞–∑–º–µ—Ä–∞ —Ñ–∞–π–ª–∞
        file_size = os.path.getsize(file_path) / (1024 * 1024)  # –≤ MB
        max_size = 50 if media_data['type'] == 'video' else 10

        if file_size > max_size:
            bot.send_message(chat_id, f"‚ö†Ô∏è –°–ª–∏—à–∫–æ–º –±–æ–ª—å—à–æ–π —Ñ–∞–π–ª ({file_size:.1f}MB)")
            return

        with open(file_path, 'rb') as file:
            if media_data['type'] == 'image':
                bot.send_photo(chat_id, file, caption=caption, reply_markup=markup)
            elif media_data['type'] == 'video':
                bot.send_video(chat_id, file, caption=caption, reply_markup=markup,
                               supports_streaming=True)
            else:
                bot.send_message(chat_id, "‚ùå –ù–µ–ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ–º—ã–π —Ç–∏–ø —Ñ–∞–π–ª–∞")

    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ –º–µ–¥–∏–∞: {e}")
        bot.send_message(chat_id, "‚ö†Ô∏è –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –º–µ–¥–∏–∞—Ñ–∞–π–ª–∞")


